import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { generateCode } from '../utils/generateCode';
import { Light as SyntaxHighlighter } from 'react-syntax-highlighter';
import python from 'react-syntax-highlighter/dist/esm/languages/hljs/python';
import java from 'react-syntax-highlighter/dist/esm/languages/hljs/java';
import php from 'react-syntax-highlighter/dist/esm/languages/hljs/php';
import { atomOneDark } from 'react-syntax-highlighter/dist/esm/styles/hljs';
import { makeStyles } from '@material-ui/core/styles';



SyntaxHighlighter.registerLanguage('python', python);
SyntaxHighlighter.registerLanguage('java', java);
SyntaxHighlighter.registerLanguage('php', php);


const useStyles = makeStyles({
  codeViewer: {
    position: 'fixed',
    bottom: 0,
    right: 0,
    width: '400px',
    height: '300px',
    backgroundColor: '#1f1f1f',
    border: '1px solid #333',
    borderRadius: '10px 0 0 0',
    cursor: 'pointer',
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
    zIndex: 1000,
    boxShadow: '0 0 20px rgba(0, 0, 0, 0.3)',
  },
  fullScreen: {
    width: '100%',
    height: '100%',
    top: 0,
    left: 0,
    borderRadius: 0,
  },
  languageSelector: {
    padding: '10px',
    backgroundColor: '#2d2d2d',
    display: 'flex',
    justifyContent: 'flex-end',
    borderBottom: '1px solid #444',
  },
  select: {
    backgroundColor: '#333',
    color: '#00ff9f',
    border: '1px solid #555',
    borderRadius: '4px',
    padding: '5px 10px',
    outline: 'none',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: '#444',
    }
  },
});

const CodeViewer = () => {


  const classes = useStyles();
  const [code, setCode] = useState('');
  const [isFullScreen, setIsFullScreen] = useState(false);
  const [language, setLanguage] = useState('Python');

  const classesState = useSelector((state) => state.classes);
  const relationshipsState = useSelector((state) => state.relationships);

  useEffect(() => {

    const diagramData = { classes: classesState, relationships: relationshipsState };
    const generatedCode = generateCode(diagramData, language);
    setCode(generatedCode);

  }, [classesState, relationshipsState, language]);

  const handleDoubleClick = () => {
    setIsFullScreen(!isFullScreen);
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
  };

  const getLanguageSyntax = () => {
    switch (language) {
      case 'Python':
        return 'python';
      case 'Java':
        return 'java';
      case 'PHP':
        return 'php';
      default:
        return 'plaintext';
    }
  };

  return (
    <div
      className={`${classes.codeViewer} ${isFullScreen ? classes.fullScreen : ''}`}
      onDoubleClick={handleDoubleClick}
    >

      <div className={classes.languageSelector}>
        <select
          value={language}
          onChange={handleLanguageChange}
          className={classes.select}
        >
          <option value="Python">Python</option>
          <option value="Java">Java</option>
          <option value="PHP">PHP</option>
        </select>
      </div>
      <SyntaxHighlighter
        language={getLanguageSyntax()}
        style={atomOneDark}
        customStyle={{ backgroundColor: '#000', color: '#0f0', margin: 0, padding: '10px' }}
        wrapLines={true}
        lineProps={{ style: { whiteSpace: 'pre-wrap' } }}
      >
        {code}
      </SyntaxHighlighter>
    </div>
  );
};

export default CodeViewer;
